import React, { useEffect, useState } from "react";
import axios from "axios";
import "./gallery.css";

import { dogs, cats } from "./Data.js";

const DOG_API =
    "https://api.thedogapi.com/v1/images/search?limit=20&order=DESC";
const CAT_API =
    "https://api.thecatapi.com/v1/images/search?limit=20&order=DESC";

function pairPhotos(list, urls, species) {
    const n = Math.min(urls.length, list.length);
    return Array.from({ length: n }, (_, i) => ({
        ...list[i],
        species,
        photo: urls[i],
    }));
}

export default function Gallery() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    let alive = true;
    (async () => {
      const [dRes, cRes] = await Promise.all([axios.get(DOG_API), axios.get(CAT_API)]);
      const dogUrls = (dRes.data || []).map((d) => d.url).filter(Boolean).slice(0, 20);
      const catUrls = (cRes.data || []).map((c) => c.url).filter(Boolean).slice(0, 20);

      // 1) Build 20 dog cards + 20 cat cards (=> 40)
      const dogCards = dogs.slice(0, 20).map((p, i) => ({
        ...p,
        species: "dog",
        photo: dogUrls[i] || null,
      }));
      const catCards = cats.slice(0, 20).map((p, i) => ({
        ...p,
        species: "cat",
        photo: catUrls[i] || null,
      }));

      const all = [...dogCards, ...catCards]; // ALWAYS 40
      if (alive) setItems(all);
      // quick sanity check in devtools:
      console.log("Built cards:", { dogs: dogCards.length, cats: catCards.length, total: all.length });
    })();
    return () => { alive = false; };
  }, []);

  return (
    <section className="pet-gallery">
      <h2 className="pet-count">Showing {items.length} / 40</h2>

      <div className="pet-grid">
        {items.map((pet) => {
          const src = pet.photo || placeholder(pet.species.toUpperCase());
          return (
            <article className="pet-card" key={pet.id}>
              <img
                src={src}
                alt={`${pet.name} the ${pet.species}`}
                onError={(e) => { e.currentTarget.src = placeholder(pet.species.toUpperCase()); }}
              />
              <div className="pet-overlay">
                <p><span className="k">Name:</span> <span className="v">{pet.name}</span></p>
                <p><span className="k">Age:</span> <span className="v">{pet.age} {pet.age === 1 ? "year" : "years"}</span></p>
              </div>
            </article>
          );
        })}
      </div>
    </section>
  );
}
