// about.js
import React from "react";
import "./about.css";

export default function About({
  id = "about",                         // ← anchor target
  imageSrc = "/assets/about-hero.jpg",
  imageAlt = "Puppy cuddling a kitten on a couch",
}) {
  return (
    <section id={id} className="ph-about-hero" aria-labelledby="ph-about-title">
      <div className="ph-about-hero__media">
        <img src={imageSrc} alt={imageAlt} />
      </div>

      <div className="ph-about-hero__copy">
        <p className="ph-eyebrow">About Us</p>
        <h1 id="ph-about-title">
          Pet Heaven — <span className="nowrap">Love &amp; Adopt</span>
        </h1>
        <p>
          Pet Heaven is a community-led platform that helps abandoned cats and
          dogs find safe, loving homes. With shelters and volunteers, we make
          sure every pet gets the care and medical attention they deserve.
        </p>
        <p>
          Submit an adoption request or responsibly rehome a pet when life
          changes. Every match is a second chance at a happy life.
        </p>
      </div>
    </section>
  );
}
