import React, { useEffect, useState } from "react";
import axios from "axios";
import "./gallery.css";

import { dogs, cats } from "./Data.js";

const DOG_API =
  "https://api.thedogapi.com/v1/images/search?limit=20&page=100&order=DESC";
const CAT_API =
  "https://api.thecatapi.com/v1/images/search?limit=20&page=100&order=DESC";

function pairPhotos(list, urls, species) {
  const n = Math.min(list.length, urls.length);
  return list.slice(0, n).map((p, i) => ({
    ...p,
    species,
    photo: urls[i],
  }));
}

export default function Gallery() {
  const [state, setState] = useState({ loading: true, error: null, items: [] });

  useEffect(() => {
    let alive = true;

    (async () => {
      try {
        const [dogRes, catRes] = await Promise.all([
          axios.get(DOG_API),
          axios.get(CAT_API),
        ]);

        // Extract URLs (filter out any weird non-image URLs just in case)
        const dogUrls = (dogRes.data || [])
          .map((d) => d.url)
          .filter((u) => typeof u === "string" && !u.endsWith(".mp4"));
        const catUrls = (catRes.data || [])
          .map((c) => c.url)
          .filter((u) => typeof u === "string" && !u.endsWith(".mp4"));

        const withDogs = pairPhotos(dogs, dogUrls, "dog");
        const withCats = pairPhotos(cats, catUrls, "cat");
        const items = [...withDogs, ...withCats];

        if (alive) setState({ loading: false, error: null, items });
      } catch (err) {
        if (alive)
          setState({
            loading: false,
            error: err?.message || "Failed to load images",
            items: [],
          });
      }
    })();

    return () => {
      alive = false;
    };
  }, []);

  if (state.loading) {
    return (
      <section className="pet-gallery">
        <div className="pet-grid">
          {Array.from({ length: 12 }).map((_, i) => (
            <div className="pet-card skeleton" key={i} />
          ))}
        </div>
      </section>
    );
  }

  if (state.error) {
    return (
      <section className="pet-gallery">
        <p className="pet-error">Oops: {state.error}</p>
      </section>
    );
  }

  return (
    <section className="pet-gallery" aria-labelledby="pet-gallery-title">
      <h1 id="pet-gallery-title" className="sr-only">
        Adoptee Gallery
      </h1>

      <div className="pet-grid">
        {state.items.map((pet) => (
          <article className="pet-card" key={pet.id}>
            <img
              src={pet.photo}
              alt={`${pet.name} the ${pet.species}`}
              onError={(e) => (e.currentTarget.style.visibility = "hidden")}
            />
            <div className="pet-overlay">
              <p>
                <span className="k">Name:</span>{" "}
                <span className="v">{pet.name}</span>
              </p>
              <p>
                <span className="k">Age:</span>{" "}
                <span className="v">
                  {pet.age} {pet.age === 1 ? "year" : "years"}
                </span>
              </p>
            </div>
          </article>
        ))}
      </div>
    </section>
  );
}
