// src/components/NavBar.js
import React, { useState, useRef, useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import AuthModal, { getCurrentUser, clearCurrentUser } from "./Login";

const NavBar = () => {
  const [open, setOpen] = useState(false);       // main nav (hamburger)
  const [userOpen, setUserOpen] = useState(false); // user dropdown
  const [authOpen, setAuthOpen] = useState(false); // modal open
  const [authMode, setAuthMode] = useState("login"); // "login" | "register"
  const [currentUser, setCurrentUser] = useState(getCurrentUser()); // {name,email} | null

  const userRef = useRef(null);

  const closeMenu = () => {
    setOpen(false);
    setUserOpen(false);
  };

  // Close user menu when clicking outside
  useEffect(() => {
    const onDocClick = (e) => {
      if (userRef.current && !userRef.current.contains(e.target)) {
        setUserOpen(false);
      }
    };
    document.addEventListener("mousedown", onDocClick);
    return () => document.removeEventListener("mousedown", onDocClick);
  }, []);

  // Close user menu on Escape
  const onUserKeyDown = (e) => {
    if (e.key === "Escape") setUserOpen(false);
  };

  return (
    <nav className="nav" aria-label="Main">
      <div className="nav-inner">
        <Link to="/" className="brand" onClick={closeMenu}>
          Pet Heaven
        </Link>

        <button
          className="nav-toggle"
          aria-label="Toggle menu"
          aria-expanded={open}
          onClick={() => setOpen(!open)}
        >
          <svg width="28" height="28" viewBox="0 0 24 24" aria-hidden="true">
            <path d="M3 6h18M3 12h18M3 18h18"
              stroke="currentColor" strokeWidth="2" fill="none"
              strokeLinecap="round" />
          </svg>
        </button>

        <ul className={`nav-links ${open ? "open" : ""}`}>
          <li>
            <NavLink to="/" end className={({ isActive }) => isActive ? "active" : undefined} onClick={closeMenu}>
              Home
            </NavLink>
          </li>
          <li>
            <NavLink to="/services" className={({ isActive }) => isActive ? "active" : undefined} onClick={closeMenu}>
              Service
            </NavLink>
          </li>
          <li>
            <NavLink to="/contact" className={({ isActive }) => isActive ? "active" : undefined} onClick={closeMenu}>
              Contact Us
            </NavLink>
          </li>
        </ul>

        {/* User menu */}
        <div className="nav-user" ref={userRef} onKeyDown={onUserKeyDown}>
          <button
            className="nav-login"
            aria-haspopup="menu"
            aria-expanded={userOpen}
            aria-label="Open user menu"
            onClick={() => setUserOpen((v) => !v)}
          >
            <svg width="28" height="28" viewBox="0 0 24 24" aria-hidden="true">
              <path
                d="M12 12a5 5 0 1 0-5-5 5 5 0 0 0 5 5zm0 2c-4.418 0-8 2.239-8 5v1h16v-1c0-2.761-3.582-5-8-5z"
                fill="currentColor"
              />
            </svg>
          </button>

          <div className={`user-menu ${userOpen ? "open" : ""}`} role="menu">
            <Link to="/login" className="user-menu-item" role="menuitem" onClick={closeMenu}>
              Member Login
            </Link>
            <Link to="/register" className="user-menu-item" role="menuitem" onClick={closeMenu}>
              Register
            </Link>
          </div>
        </div>
      </div>
      
    </nav>
  );
};

export default NavBar;
