import React, { useEffect, useState } from "react";
import axios from "axios";
import "./gallery.css";

import { dogs, cats } from "./Data.js";

const DOG_API =
    "https://api.thedogapi.com/v1/images/search?limit=20&order=DESC";
const CAT_API =
    "https://api.thecatapi.com/v1/images/search?limit=20&order=DESC";

function pairPhotos(list, urls, species) {
    const n = Math.min(urls.length, list.length);
    return Array.from({ length: n }, (_, i) => ({
        ...list[i],
        species,
        photo: urls[i],
    }));
}

export default function Gallery() {
  const [state, setState] = useState({ loading: true, error: null, items: [] });

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const [dogRes, catRes] = await Promise.all([axios.get(DOG_API), axios.get(CAT_API)]);

        const dogUrls = (dogRes.data || []).map(d => d.url).filter(Boolean);
        const catUrls = (catRes.data || []).map(c => c.url).filter(Boolean);

        const dogItems = pairPhotos(dogs, dogUrls, "dog");
        const catItems = pairPhotos(cats, catUrls, "cat");
        const items = [...dogItems, ...catItems]; // aim for 40

        if (alive) setState({ loading: false, error: null, items });
      } catch (err) {
        if (alive) setState({ loading: false, error: err?.message || "Failed to load images", items: [] });
      }
    })();
    return () => { alive = false; };
  }, []);

  if (state.loading) {
    return (
      <section className="pet-gallery">
        <h2 className="pet-count">Loading gallery…</h2>
        <div className="pet-grid">
          {Array.from({ length: 40 }).map((_, i) => <div className="pet-card skeleton" key={i} />)}
        </div>
      </section>
    );
  }

  if (state.error) {
    return <section className="pet-gallery"><p className="pet-error">Oops: {state.error}</p></section>;
  }

  return (
    <section className="pet-gallery" aria-labelledby="pet-gallery-title">
      <h1 id="pet-gallery-title" className="sr-only">Adoptee Gallery</h1>
      <h2 className="pet-count">Showing {state.items.length} photos</h2>

      <div className="pet-grid">
        {state.items.map(pet => (
          <article className="pet-card" key={pet.id}>
            {/* 3) don’t hide the whole image; add a fallback style instead */}
            <img
              src={pet.photo}
              alt={`${pet.name} the ${pet.species}`}
              onError={(e) => { e.currentTarget.classList.add("img-fallback"); }}
            />
            <div className="pet-overlay">
              <p><span className="k">Name:</span> <span className="v">{pet.name}</span></p>
              <p><span className="k">Age:</span> <span className="v">{pet.age} {pet.age === 1 ? "year" : "years"}</span></p>
            </div>
          </article>
        ))}
      </div>
    </section>
  );
}
