// src/components/auth/Login.js
import React, { useEffect, useRef, useState } from "react";
import "./login.css";

/** localStorage keys */
const LS_USERS = "phs_users";         // array of {name,address,email,password}
const LS_CURRENT_USER = "phs_currentUser"; // {name,email}

/** helpers */
const readUsers = () => {
    try { return JSON.parse(localStorage.getItem(LS_USERS)) || []; }
    catch { return []; }
};
const writeUsers = (users) => localStorage.setItem(LS_USERS, JSON.stringify(users));
const setCurrentUser = (user) => localStorage.setItem(LS_CURRENT_USER, JSON.stringify(user));
export const getCurrentUser = () => {
    try { return JSON.parse(localStorage.getItem(LS_CURRENT_USER)); }
    catch { return null; }
};
export const clearCurrentUser = () => localStorage.removeItem(LS_CURRENT_USER);

const validateEmail = (email) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(email).toLowerCase());

/**
 * AuthModal
 * props:
 *  - open: boolean
 *  - mode: "login" | "register"
 *  - onClose(): void
 *  - onAuthed(user): void  (called after successful login/register)
 */
export default function AuthModal({ open, mode = "login", onClose, onAuthed }) {
    const [active, setActive] = useState(mode);
    const [loading, setLoading] = useState(false);
    const [msg, setMsg] = useState(null);
    const dialogRef = useRef(null);

    useEffect(() => setActive(mode), [mode]);
    // Close on ESC
    useEffect(() => {
        const onKey = (e) => { if (e.key === "Escape") onClose?.(); };
        if (open) document.addEventListener("keydown", onKey);
        return () => document.removeEventListener("keydown", onKey);
    }, [open, onClose]);

    useEffect(() => { if (open) setMsg(null); }, [open, active]);

    if (!open) return null;

    const handleBackdrop = (e) => {
        if (dialogRef.current && !dialogRef.current.contains(e.target)) onClose?.();
    };

    // ---- Register ----
    const handleRegister = async (e) => {
        e.preventDefault();
        setMsg(null);

        const fd = new FormData(e.currentTarget);
        const name = (fd.get("name") || "").trim();
        const address = (fd.get("address") || "").trim();
        const email = (fd.get("email") || "").trim().toLowerCase();
        const password = (fd.get("password") || "").trim();

        if (!name || !address || !email || !password) {
            setMsg({ type: "error", text: "All fields are required." });
            return;
        }
        if (!validateEmail(email)) {
            setMsg({ type: "error", text: "Please enter a valid email." });
            return;
        }
        if (password.length < 6) {
            setMsg({ type: "error", text: "Password should be at least 6 characters." });
            return;
        }

        setLoading(true);
        try {
            const users = readUsers();
            if (users.some(u => u.email === email)) {
                setMsg({ type: "error", text: "Email is already registered." });
                setLoading(false);
                return;
            }
            const newUser = { name, address, email, password };
            users.push(newUser);
            writeUsers(users);
            setCurrentUser({ name, email });
            onAuthed?.({ name, email });
            onClose?.();
        } finally {
            setLoading(false);
        }
    };

    // ---- Login ----
    const handleLogin = async (e) => {
        e.preventDefault();
        setMsg(null);

        const fd = new FormData(e.currentTarget);
        const email = (fd.get("email") || "").trim().toLowerCase();
        const password = (fd.get("password") || "").trim();

        if (!email || !password) {
            setMsg({ type: "error", text: "Email and password are required." });
            return;
        }
        if (!validateEmail(email)) {
            setMsg({ type: "error", text: "Please enter a valid email." });
            return;
        }

        setLoading(true);
        try {
            const users = readUsers();
            const found = users.find(u => u.email === email && u.password === password);
            if (!found) {
                setMsg({ type: "error", text: "Invalid email or password." });
                setLoading(false);
                return;
            }
            setCurrentUser({ name: found.name, email: found.email });
            onAuthed?.({ name: found.name, email: found.email });
            onClose?.();
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-backdrop" onMouseDown={handleBackdrop}>
            <section
                className="auth-modal"
                role="dialog"
                aria-modal="true"
                aria-labelledby="auth-title"
                ref={dialogRef}
                onMouseDown={(e) => e.stopPropagation()}
            >
                <header className="auth-header">
                    <button
                        className={`auth-tab ${active === "login" ? "active" : ""}`}
                        onClick={() => setActive("login")}
                    >
                        Login
                    </button>
                    <button
                        className={`auth-tab ${active === "register" ? "active" : ""}`}
                        onClick={() => setActive("register")}
                    >
                        Register
                    </button>

                    <button className="auth-close" onClick={onClose} aria-label="Close">×</button>
                </header>

                {msg && (
                    <div className={`auth-alert ${msg.type}`}>{msg.text}</div>
                )}

                {active === "register" ? (
                    <form className="auth-form" onSubmit={handleRegister}>
                        <label>
                            <span>Name</span>
                            <input name="name" type="text" placeholder="Your full name" required />
                        </label>
                        <label>
                            <span>Address</span>
                            <input name="address" type="text" placeholder="123 Pet St" required />
                        </label>
                        <label>
                            <span>Email</span>
                            <input name="email" type="email" placeholder="you@example.com" required />
                        </label>
                        <label>
                            <span>Password</span>
                            <input name="password" type="password" placeholder="••••••••" required />
                        </label>

                        <button className="auth-btn" type="submit" disabled={loading}>
                            {loading ? "Creating account…" : "Create account"}
                        </button>
                    </form>
                ) : (
                    <form className="auth-form" onSubmit={handleLogin}>
                        <label>
                            <span>Email</span>
                            <input name="email" type="email" placeholder="you@example.com" required />
                        </label>
                        <label>
                            <span>Password</span>
                            <input name="password" type="password" placeholder="••••••••" required />
                        </label>

                        <button className="auth-btn" type="submit" disabled={loading}>
                            {loading ? "Signing in…" : "Sign in"}
                        </button>
                    </form>
                )}
            </section>
        </div>
    );
}
