// about.js
import React from "react";

export default function About({
  imageSrc = "/images/about-hero.jpg", // ← change to your image path
  imageAlt = "Puppy cuddling a kitten on a couch",
}) {
  return (
    <>
      <style>{`
        .about-hero{display:flex;align-items:center;gap:48px;max-width:1200px;margin:40px auto;padding:0 24px;}
        .about-hero__media{flex:1;min-width:300px}
        .about-hero__media img{width:100%;height:100%;max-height:460px;object-fit:cover;border-radius:16px}
        .about-hero__copy{flex:1;font-size:18px;line-height:1.7}
        .about-hero h2{margin:0;font-size:18px;letter-spacing:.12em;text-transform:uppercase;opacity:.7}
        .about-hero h1{margin:8px 0 20px;font-size:42px}
        .about-hero strong{white-space:nowrap}
        @media (max-width:900px){
          .about-hero{flex-direction:column;text-align:center;gap:24px}
          .about-hero__media{order:0;min-width:auto}
          .about-hero h1{font-size:32px}
        }
      `}</style>

      <section className="about-hero">
        <div className="about-hero__media">
          <img src={imageSrc} alt={imageAlt} />
        </div>

        <div className="about-hero__copy">
          <h2>About Us</h2>
          <h1>Pet Heaven — <strong>Love &amp; Adopt</strong></h1>
          <p>
            Pet Heaven is a community-led platform helping abandoned cats and
            dogs find safe, loving homes. Working closely with shelters and
            volunteers, we make sure every pet receives the care, love, and
            medical attention they deserve.
          </p>
          <p>
            With our simple online process, you can submit an adoption request
            or responsibly rehome a pet when life changes. Every match is a
            second chance at a happy life—thanks for being part of their journey.
          </p>
        </div>
      </section>
    </>
  );
}
